package com.tmmas.scl.framework.customerdomainfacturacion.CustomerABE.bo;

import org.apache.commons.configuration.CompositeConfiguration;
import org.apache.log4j.Logger;

import com.tmmas.cl.framework20.util.UtilProperty;
import com.tmmas.scl.framework.CustomerDomain.CustomerABE.dto.OficinaDTO;
import com.tmmas.scl.framework.CustomerDomain.exception.RateUsageRecordsException;
import com.tmmas.scl.framework.customerdomainfacturacion.CustomerABE.bo.Interface.OficinaBOIT;
import com.tmmas.scl.framework.customerdomainfacturacion.CustomerABE.dao.OficinaDAO;
import com.tmmas.scl.framework.customerdomainfacturacion.CustomerABE.dao.Interface.OficinaDAOIT;

public class OficinaBO implements OficinaBOIT {
	private static Logger logger = Logger.getLogger(OficinaBO.class);

	private CompositeConfiguration config;

	public OficinaBO() {
		super();
		config = UtilProperty
				.getConfiguration("ServicioFacturacionWS.properties",
						"com/tmmas/scl/framework/properties/archivorecursos.properties");
	}


	public OficinaDTO obtenerOficina(String codUsuario)
			throws RateUsageRecordsException {
		logger.debug("obtenerOficina():start");		
		OficinaDTO result=null;
		OficinaDAOIT OficinaDAO = new OficinaDAO();
		result=OficinaDAO.obtenerOficina(codUsuario);
		logger.debug("obtenerOficina():start");		
		return result;
	
	}

}
